import { useAuth0 } from "@auth0/auth0-react";
import { ArrowLeftCircleIcon } from '@heroicons/react/20/solid'

const LoginButton = () => {
    const {loginWithRedirect, isAuthenticated, user } = useAuth0();

    return(
        !isAuthenticated && (
            <button className="text-sm font-semibold leading-6 text-gray-300 pb-[22px]" onClick={() => loginWithRedirect()}>
                {ArrowLeftCircleIcon} Log In  <span aria-hidden="true">&rarr;</span>
            </button>
        )

    )
}

export default LoginButton
